// A sample structure based on the PDF
export const mockDishes = [
  {
    id: 1,
    name: "Kadhai Paneer",
    description: "Paneer cubes in spicy onion gravy with capsicum.",
    image: "https://via.placeholder.com/300x200.png?text=Kadhai+Paneer",
    mealType: "MAIN COURSE",
    type: "VEG",
    ingredients: [
      { name: "Paneer", quantity: "200g" },
      { name: "Onion", quantity: "2 large" },
      { name: "Capsicum", quantity: "1 large" },
      { name: "Tomato Puree", quantity: "1 cup" }
    ]
  },
  {
    id: 2,
    name: "Chicken Biryani",
    description: "Fragrant rice with marinated chicken and spices.",
    image: "https://via.placeholder.com/300x200.png?text=Chicken+Biryani",
    mealType: "MAIN COURSE",
    type: "NON-VEG",
    ingredients: [
      { name: "Chicken", quantity: "500g" },
      { name: "Basmati Rice", quantity: "2 cups" },
      { name: "Spices", quantity: "Mixed" }
    ]
  },
  {
    id: 3,
    name: "Spring Rolls",
    description: "Crispy rolls filled with vegetables.",
    image: "https://via.placeholder.com/300x200.png?text=Spring+Rolls",
    mealType: "STARTER",
    type: "VEG",
    ingredients: [
      { name: "Cabbage", quantity: "1 cup" },
      { name: "Carrot", quantity: "1 cup" },
      { name: "Spring Roll Sheets", quantity: "6 pcs" }
    ]
  }
];
